async function submitForm(){const operandA=parseFloat(document.getElementById("operandA").value),operandB=parseFloat(document.getElementById("operandB").value);if(isNaN(operandA)||isNaN(operandB))return void(document.getElementById("result").innerText="Please enter valid numbers.");const operations=["add","subtract","multiply","divide"];let results=[];for(const operation of operations)try{const response=await fetch(`http://localhost:8000/${operation}`,{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({a:operandA,b:operandB})});if(!response.ok)throw new Error("Failed to fetch");const data=await response.json();results.push(data)}catch(error){results.push({operation:operation,error:error.message})}displayResult(results)}function displayResult(data){let resultHTML="";for(const item of data)item.error?resultHTML+=`<p>${item.operation} error: ${item.error}</p>`:resultHTML+=`<p>${item.operation} result: ${item.result}</p>`;document.getElementById("result").innerHTML=resultHTML}