document.addEventListener("DOMContentLoaded",(async function(){const studentsTableBody=document.getElementById("studentsTable").querySelector("tbody");try{const response=await fetch("http://localhost:8000/students");(await response.json()).forEach(((student,index)=>{const row=document.createElement("tr");row.innerHTML=`\n                <td>${index+1}</td>\n                <td>${student.name}</td>\n                <td>${student.age}</td>\n                <td>${student.address}</td>\n                <td>${student.phone}</td>\n                <td>${student.email}</td>\n                <td>${student.class}</td>\n            `,studentsTableBody.appendChild(row)}))}catch(error){console.error("Error:",error)}}));